public class Factura implements Serializable{
	public double valor;
	public Cliente cliente;
	public Pedido pedido;
	public FeedBack feedBack;

    public Factura() {
    }

    public Factura(double valor, Cliente cliente, Pedido pedido) {
        this.valor = valor;
        this.cliente = cliente;
        this.pedido = pedido;
    }
    
    public Factura(double valor, Cliente cliente, Pedido pedido, String calificacion, String observacion) {
        this.valor = valor;
        this.cliente = cliente;
        this.pedido = pedido;
        this.feedBack = new FeedBack(calificacion, observacion);
    }
        
    public void agregarFeedback(String calificacion, String observacion) {
	this.feedBack = new FeedBack(calificacion, observacion);
    }

    public double getValor() {
        return valor;
    }

    public void setValor(double valor) {
        this.valor = valor;
    }

    public Cliente getCliente() {
        return cliente;
    }

    public void setCliente(Cliente cliente) {
        this.cliente = cliente;
    }

    public Pedido getPedido() {
        return pedido;
    }

    public void setPedido(Pedido pedido) {
        this.pedido = pedido;
    }

    public FeedBack getFeedBack() {
        return feedBack;
    }

    public void setFeedBack(FeedBack feedBack) {
        this.feedBack = feedBack;
    }

    @Override
    public String toString() {
        return "Factura{" + "valor=" + valor + ", cliente=" + cliente + ", pedido=" + pedido + ", feedBack=" + feedBack + '}';
    }
    
}
